class Solution:
    # @param A : list of integers
    # @return A after the sort
    def sortColors(self, A):
        
        endR = 0
        startB = len(A)-1
        i= 0
        while i<=startB:
            if A[i]==0:
                A[i],A[endR] = A[endR],A[i]
                endR+=1
                i+=1
            elif A[i]==1:
                i+=1
            elif A[i]==2:
                A[i],A[startB] = A[startB],A[i]
                startB-=1
        return A
